# Cull out any data set already in repo:
CetaceaFileList <- setdiff(CetaceaFileList, gsub(".xml", "", list.files("/Users/eargtl/Dropbox/Mammal_Supertree/ProjectBlackFish/Input data/XML")))

# If files to copy across are present:
if (length(CetaceaFileList) > 0) {
  
  # Copy across NEXUS files from Graeme's local repo to GitHub repo:
  x <- lapply(as.list(CetaceaFileList), function(x) file.copy(from = paste("~/Documents/Homepage/www.graemetlloyd.com/nexus/", x, ".nex", sep = ""), to = paste("~/Dropbox/Mammal_Supertree/ProjectBlackFish/Input data/NEXUS/", x, ".nex", sep = ""), overwrite = TRUE))
  
  # Copy across MRP files from Graeme's local repo to GitHub repo:
  x <- lapply(as.list(CetaceaFileList), function(x) file.copy(from = paste("~/Documents/Homepage/www.graemetlloyd.com/mrp/", x, "mrp.nex", sep = ""), to = paste("~/Dropbox/Mammal_Supertree/ProjectBlackFish/Input data/MRP/", x, "mrp.nex", sep = ""), overwrite = TRUE))
  
  # Copy across PDF files from Graeme's local repo to GitHub repo:
  x <- lapply(as.list(unique(unlist(lapply(as.list(CetaceaFileList), function(x) {y <- strsplit(x, split = "[:0-9:]{4}|inpress")[[1]]; if(nchar(y[2]) == 2) {paste(strsplit(x, "")[[1]][1:(nchar(x) - 1)], collapse = "")} else {x}})))), function(x) {dir.create(paste("~/Dropbox/Mammal_Supertree/ProjectBlackFish/Input data/PDF/", x, "/", sep = "")); FilesToCopy <- list.files(paste("~/Documents/Homepage/www.graemetlloyd.com/Added/", x, "/", sep = "")); file.copy(from = paste(paste("~/Documents/Homepage/www.graemetlloyd.com/Added/", x, "/", sep = ""), FilesToCopy, sep = ""), to = paste(paste("~/Dropbox/Mammal_Supertree/ProjectBlackFish/Input data/PDF/", x, "/", sep = ""), FilesToCopy, sep = ""), overwrite = TRUE)})
  
  # Copy across XML files from Graeme's local repo to GitHub repo:
